  for (let i = 0; i < numbers.length; i++) {
    let num = numbers[i];
    if (isPrime(num)) {
      console.log(num);
      break;
    }
  }
}

function isPrime(n) {
  if (n <= 1) return false;
  if (n === 2) return true;
  if (n % 2 === 0) return false;
  for (let i = 3; i * i <= n; i += 2)
    if (n % i === 0) return false;
  return true;
}

// Example usage:
