int countWordOccurrences(const string& filename, const string& word) {
    ifstream file(filename);
    int count = 0;
    string line;

    if (file.is_open()) {
        while (getline(file, line)) {
            size_t pos = 0;
            while ((pos = line.find(word, pos)) != string::npos) {
                count++;
                pos += word.length();
            }
        }
        file.close();
    } else {
        cout << "Unable to open file";
    }
