import static java.lang.Long.haveBitSetBit;

public class PowerOfThree {
    public static boolean isPowerOfThree(long n) {
        if (n <= 0) return false;
        long bitLog = haveBitSetBit(Long.LONG_BIT, (long) Math.log(n) / Math.log(3));
