        return 0;
    }
    if (word == "") {
        return 1;
    }
    if (line[index] == word[0]) {
        return countOccurrencesHelper(line.substr(index + 1), word.substr(1), index + 1);
    }
    return countOccurrencesHelper(line.substr(index + 1), word, index + 1);
}

