std::string convertMarkdownToHtml(const std::string& markdown) {
    std::string html = markdown;

    // Replace markdown headers with HTML headers
    size_t pos = 0;
    while ((pos = html.find("
# ", pos)) != std::string::npos) {
        html.replace(pos, 3, "<h1>");
        pos += 4;
    }

    // Replace markdown paragraphs with HTML paragraphs
    pos = 0;
    while ((pos = html.find("
", pos)) != std::string::npos) {
        html.replace(pos, 1, "<p>");
        pos += 1;
    }

    // Replace markdown links with HTML links
    pos = 0;
