```python
def print_sudoku(grid):
    for row in range(9):
        if row % 3 == 0 and row != 0:
            print("- - - - - - - - - - - -")
        for col in range(9):
            if col % 3 == 0 and col != 0:
                print("| ", end="")
            if col == 8:
                print(grid[row][col])
            else:
                print(grid[row][col], end=" | ")

def find_empty_location(grid):
    for row in range(9):
        for col in range(9):
            if grid[row][col] == 0:
                return (row, col)
    return None

def used_in_row(grid, row, num):
    for i in range(9):
        if grid[row][i] == num:
            return True
    return False

def used_in_col(grid, col, num):
    for i in range(9):
        if grid[i][col] == num:
            return True
    return False

def used_in_box(grid, row, col, num):
    for i in range(3):
        for j in range(3):
            if grid[i + row][j + col] == num:
                return True
    return False

def check_location_is_safe(grid, row, col, num):
    return not used_in_row(grid, row, num) and not used_in_col(grid, col, num) and not used_in_box(grid, row - row % 3, col - col % 3, num)

def solve_sudoku(grid):
    find = find_empty_location(grid)
    if not find:
        return True
    else:
        row, col = find
    for i in range(1,10):
        if check_location_is_safe(grid, row, col, i):
            grid[row][col] = i
            if solve_sudoku(grid):
                return True
            grid[row][col] = 0
    return False

if __name__ == "__main__":
    grid = [[1, 0, 5, 0, 0, 0, 0, 8, 0],
            [0, 0, 0, 0, 3, 0, 0, 6, 4],
            [8, 3, 4, 6, 7, 0, 9, 0, 0],
            [2, 9, 1, 0, 0, 8, 3, 7, 6],
            [0, 0, 6, 0, 0, 9, 8, 1, 0],
            [0, 0, 0, 0, 0, 2, 0, 0, 0],
            [0, 2, 0, 0, 9, 7, 6, 0, 0],
            [5, 4, 0, 0, 0, 0, 0, 9, 8],
            [6, 1, 0, 0, 0, 5, 4, 3, 7]]
    solve_sudoku(grid)
    print_sudoku(grid)
```

