const { Command } = require("discord.js-commando");

module.exports = class BanCommand extends Command {
  constructor(client) {
    super(client, {
      name: "ban",
      aliases: ["kick"],
      group: "moderation",
      memberName: "ban",
      description: "Bans a specified member from the server.",
      examples: ["ban @user"],
      throttling: {
        usages: 2,
        duration: 3,
      },
