    std::regex markdown_regex("(?<=^|<<)([^<<]+)(?=|>>>)");
    std::smatch match;
    if (std::regex_search(markdown, match, markdown_regex)) {
        // Found a match, so the markdown has been converted to HTML.
        return match[1];
    } else {
        // No match found, so the markdown is not HTML formatted.
        return markdown;
    }
}

