  params: {
    pageSize: SEARCH_RESULTS_PER_PAGE,
    page: 1,
    orderBy: orderBy,
    sortOrder: sortOrder,
    intitle: searchTerm,
    tags: tags.join(','),
    site: siteName,
    ...(tags.length > 0 && { [SHOW_ADVANCED_SEARCH_ENDPOINT]: true }), // show advanced search options if tags are provided
  },
