  if (num <= 1) return false;
  for (let I = 2; I < num; i++) {
    if (num % I === 0) return false;
  }
  return true;
}

function findPrimeNumbers(numbers) {
  for (let I = 0; I < numbers.length; i++) {
    const number = parseInt(numbers[i]);
    if (Number.isNaN(number) || !Number.isInteger(number) || number < 2) continue; // skip non-integer or non-positive values
    if (isPrime(number)) {
      console.log(`${number} is a prime number`);
    }
  }
}

