const client = new Client({ intents: ['GUILDS', 'GUILD_MESSAGES', 'GUILD_MEMBERS'] });

client.commands = [];

const banCommand = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Bans a user')
  .addUserOption((option) => option
    .setName('user')
    .setDescription('The user to ban')
    .setRequired(true));

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;
  const { commandName } = interaction;
  const command = client.commands.find((cmd) => cmd.name === commandName);
  if (!command) return;
  await interaction.deferReply();
  switch (commandName) {
    case 'ban': {
      const user = interaction.options.getUser('user');
      if (!user) {
        return interaction.reply('Please provide a user to ban.');
      }
      const guild = interaction.guild;
      const member = guild.members.cache.get(user.id);
      if (!member) {
        return interaction.reply('The user is not a member of this guild.');
      }
      if (member.permissions.has('BAN_MEMBERS')) {
        return interaction.reply('You cannot ban yourself.');
      }
      await member.ban();
      interaction.reply(`User ${member.user.tag} has been banned from the server.`);
      break;
    }
    default: {
      return interaction.reply('That command does not exist.');
    }
  }
});
