#include <string>
#include <boost/algorithm/string.hpp>

#include <boost/lexical_cast.hpp> // for boost::algorithm::replace_all_copy

#include <md/html_output.h> // for md::html_output_options and md::to_html()

namespace md = ::md;
namespace ba = boost::algorithm;

std::string markdownToHtml(const std::string& markdownText) {
    std::string htmlText;

    md::html_output_options options;
    options.escape_markup = true; // enable Markdown character escaping
    md::to_html(markdownText, options, std::back_inserter(htmlText));

    // Replace Markdown special characters with their corresponding HTML equivalents
    ba::replace_all_copy(
        htmlText,  // Target string to modify in place
        "\n",      // Search substring (newline)
        "<br/>\n"  // Replacement string (HTML line break followed by newline)
    );
    ba::replace_all_copy(htmlText, "[[", "<span class=\"markdown-inline markdown-link\">[</span>");
    ba::replace_all_copy(htmlText, "]]", "<span class=\"markdown-inline markdown-link\"></span>");
    ba::replace_all_copy(htmlText, "(", "<span class=\"markdown-inline\">(");
    ba::replace_all_copy(htmlText, ")", "</span>)");
    ba::replace_all_copy(htmlText, "*", "<em>");
    ba::replace_all_copy(htmlText, "_", "<strong>");
    ba::replace_all_copy(htmlText, "`", "<code>");
    ba::replace_all_copy(htmlText, "\\.", "&amp;."); // Escape literal dot character with HTML entity (&amp;)
    ba::replace_all_copy(htmlText, "\\(", "&amp;lt;"); // Escape literal left parenthesis character with HTML entity (&amp;lt;)
    ba::replace_all_copy(htmlText, "\\)", "&amp;gt;"); // Escape literal right parenthesis character with HTML entity (&amp;gt;)

    return htmlText;
}

