class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        self.stack2.append(item)

    def dequeue(self):
        if not self.is_empty():
            # Transfer elements from stack2 to stack1 if stack1 is empty
            while self.stack1:
                self.stack2.append(self.stack1.pop())
            return self.stack1.pop()

