#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string target_word; // store the specific word to search for
    int occurrences = 0; // initialize the counter variable

    std::cout << "Enter the target word to search for: ";
    std::cin >> target_word; // prompt user input for target word

    // Open input file for reading
