```gdscript
extends KinematicBody

var camera_node = null
var player_speed = 100
var mouse_look_speed = 2

func _ready(): void {
    # Get the camera node from the scene
    camera_node = get_node("camera")
}

func _process(delta: float): void {
    # Rotate based on mouse movement
    var mouse_x = Input.get_mouse_x()
    var mouse_y = Input.get_mouse_y()

    var delta_rotation_y = mouse_y * mouse_look_speed * delta
    var delta_rotation_x = mouse_x * mouse_look_speed * delta

    rotate_y(delta_rotation_y)
    rotate_x(math.deg2rad(-delta_rotation_x))

    # Move forward/backward based on keyboard input
    if Input.is_action_pressed("ui_move_forward"):
        move_forward(player_speed * delta)
    else if Input.is_action_pressed("ui_move_backward"):
        move_backward(player_speed * delta)

    # Move left/right based on keyboard input
    if Input.is_action_pressed("ui_move_left"):
        move_left(player_speed * delta)
    else if Input.is_action_pressed("ui_move_right"):
        move_right(player_speed * delta)
}
```

