    System.out.println("str1 and str2 are equal using == operator");
} else {
    System.out.println("str1 and str2 are not equal using == operator");
}

// Using equals() method to compare strings (recommended)
if (str1.equals(str2)) { // true, both strings have the same content
    System.out.println("str1 and str2 are equal using equals() method");
} else {
    System.out.println("str1 and str2 are not equal using equals() method");
}

// Using == operator with a different string reference (not recommended)
String str4 = "world";
if (str1 == str4) { // false, str1 and str4 reference different objects in memory
    System.out.println("str1 and str4 are equal using == operator");
} else {
    System.out.println("str1 and str4 are not equal using == operator");
}

