```cpp
#include <string>
#include <stdexcept>

#include <libmarkdown/markdown.hpp>

namespace {
// Helper function to replace special characters in Markdown with HTML entities.
std::string escapeMarkdown(std::string text) {
    // Replace common special characters with their HTML entity equivalents.
    std::replace(text.begin(), text.end(), '\n', "\\n"); // line break
    std::replace(text.begin(), text.end(), '\r', "\\r"); // carriage return
    std::replace(text.begin(), text.end(), '&', "&amp;"); // ampersand
    std::replace(text.begin(), text.end(), '<', "&lt;"); // less than sign
    std::replace(text.begin(), text.end(), '>', "&gt;"); // greater than sign
    return text;
}
}  // namespace anonymous

std::string markdownToHtml(std::string& markdownText) {
    if (markdownText.empty()) {
        throw std::runtime_error("Markdown text cannot be empty");
    }

    // Create a Markdown parser instance and convert the input Markdown to HTML.
    mdkd::parser parser;
    mdkd::options options;  // Use default options (e.g., GFM).
    auto html = parser.parse(markdownText, options);  // Throws on parsing errors.

    // Escape any remaining special characters that were not handled by Markdown parser.
    html = escapeMarkdown(std::move(html));

    return html;
}
```

